import React from "react";
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import s from "./review.module.css";
import SliderTitle from "../../components/sliderTitle/sliderTitle";

const Review = () => {
  const reviews = [
    {
      className: "itemSlider",
      reviewText: "everything is great",
      customer: "Melissa, Yandex",
    },
    {
      className: "itemSliderTwo",
      sliderTitle: "Made for Any Creative Brands",
      customButtonTitle: "Learn more",
    },
    {
      className: "itemSliderThree",
      sliderTitle: "Showcase Your Products Elegantly",
      customButtonTitle: "see all features",
    },
  ];
  const settings = {
    dots: true,
    infinite: true,
    speed: 1000,
    slidesToShow: 1,
    slidesToScroll: 1,
    autoplay: true,
    autoplaySpeed: 5000,
  };

  return (
    <div className={s.mainContainer}>
      <Slider {...settings}>
        {items.map((reviews, index) => (
          <ReviewItem item={item} />
        ))}
      </Slider>
    </div>
  );
};

export default Review;

