import React from 'react';
import "slick-carousel/slick/slick.css";
import "slick-carousel/slick/slick-theme.css";
import Slider from "react-slick";
import s from './review.module.css'
import SliderTitle from "../../components/sliderTitle/sliderTitle";


const Review = () => {

    const items = [
        {className:'itemSlider', sliderTitle: 'Welcome to Primrose',customButtonTitle: 'Browse Our products'},
        {className:'itemSliderTwo', sliderTitle: 'Made for Any Creative Brands',customButtonTitle: 'Learn more'},
        {className:'itemSliderThree', sliderTitle: 'Showcase Your Products Elegantly',customButtonTitle: 'see all features'}
    ]
    const settings = {
        dots: true,
        infinite: true,
        speed: 1000,
        slidesToShow: 1,
        slidesToScroll: 1,
        autoplay: true,
        autoplaySpeed: 5000,
    };

    return (
        <div className={s.mainContainer}>
            <Slider {...settings}>
                {items.map((item, index)=> <Item item={item} />)}
            </Slider>
        </div>
    );
};

export default Review;



const ReviewItem = ({item}) => {
    return <div className={`${s.commonItemStyles} ${s[item.className]}`}>
            <SliderTitle title={item.sliderTitle}/>
            <CustomButton title={item.customButtonTitle}/>
        </div>
   }
